<script>    export default {        name: "AddTableFieldsForm",        props: ['field_item', 'table_name'],        data: () => ({            showForm: false,            fieldsList : [],            fieldItem  : {                name : '',                type : 'varchar',                default_value: '',            },        }),        created() {            this.addFieldToArray();        },        methods: {            addFieldToArray() {                let fieldItem = Object.assign({}, this.fieldItem);                if(this.field_item && this.field_item.name)                    fieldItem = this.field_item;                this.fieldsList.push(fieldItem);            },            fieldsEmit(){                const fieldsList = Object.assign({}, this.fieldsList);                this.clearArray();                this.$emit('save_new_fields', fieldsList);            },            clearArray() {                this.fieldsList = [];                this.addFieldToArray();            },        }    }</script><template><div>    <!--<h4 class="card-title">Simple Editable table</h4>-->    <!--<h6 class="card-subtitle">Just click on word which you want to change and enter</h6>-->    <div style="display: flex; margin-bottom: 10px">        <button @click="addFieldToArray()"                 type="button" class="btn btn-outline-info"                 style="border-radius: 0px; margin-bottom: 0px">Добавить поле</button>        <button @click="clearArray()"                type="button" class="btn btn-outline-info"                style="border-radius: 0px; margin: 0px 0px 0px 15px">Очистить форму</button>    </div>    <div class="table-responsive">            <table  class="table editable-table table-bordered table-striped m-b-0 add-fileds-form-table"                   style="cursor: pointer;">                <thead>                    <tr>                        <th>Имя поля</th>                        <th>Тип поля</th>                        <th>Значение по умолчанию</th>                    </tr>                </thead>                <tbody>                    <tr v-for="(item, i) in fieldsList" :key="i">                        <td tabindex="1">                            <input v-model="item.name" type="text" >                        </td>                        <td tabindex="1">                            <select v-model="item.type" style="cursor: pointer;"                                    class="form-control form-control-line pl-0">                                    <option v-for="option in tableFieldTypes"                                            :value="option.name">{{ option.title }}                                    </option>                            </select>                        </td>                        <td tabindex="1">                            <input v-model="item.default_value" type="text" >                        </td>                    </tr>                </tbody>                <tfoot >                    <tr>                        <th style="border:0px; padding:0px;"  ></th>                        <th  style="border:0px;" ></th>                        <th style="border:0px; padding:0px; text-align: right"  >                            <button @click="fieldsEmit()"                                    type="button" class="btn btn-outline-info"                                    style="border-radius: 0px; margin-top: 10px">Сохранить данные</button>                        </th>                    </tr>                </tfoot>            </table>        </div> <!-- /.table-responsive --> </div></template><style scoped>    .add-fileds-form-table th,    .add-fileds-form-table td {        padding:2px;    }    .add-fileds-form-table input {        width: 100%;        height: 35px;        border: 1px #b8daff solid;    }</style>